import React, { useEffect } from "react";
import { cleanBasket, getBasket, removeFromBasket } from "../../http/productsAPI";
import styles from './basket.module.scss'
import s from '../../App.module.scss'
import { useNavigate } from "react-router-dom";
import { PRODUCTS_ROUTE, REGISTRATION_ROUTE } from "../../utils/consts";

const Basket = (props) =>  {
    const navigate = useNavigate();
    const productsPage = props.productsPage;
    const setBaskets = props.setBaskets;

    useEffect(() => {
        getBasket().then(data => {
            setBaskets(data)
        })
    }, [])

    let prices = 0;
    {productsPage.baskets.map(price =>
        
        prices = parseFloat((prices + price.product.price).toFixed(10))
    )}

    const cleanAllBasket = () => {
        cleanBasket().then(data => {
            setBaskets([])
        })
    }

    const removeBasketItem = (id) => {
        removeFromBasket(id).then(data => {
            alert(`Товар ${product.title} был удалён из корзины!`)
        })
    }

