import React, { useState } from 'react';
import styles from './Navbar.module.scss'
import s from "../../App.module.scss"
import { NavLink, useNavigate } from 'react-router-dom';
import { ADMIN_ROUTE, BASKET_ROUTE, LOGIN_ROUTE, REGISTRATION_ROUTE, SHOP_ROUTE } from '../../utils/consts';

const NavBar = (props) => {
    const state = props.userPage;
    const setIsAuth = props.setIsAuth;
    const setIsAdmin = props.setIsAdmin;
    const navigate = useNavigate();

    const [search, setSearch] = useState('');
    
    const logout = () => {
        localStorage.removeItem('token');
        localStorage.removeItem('isAdmin');
        setIsAuth(!!localStorage.getItem('token'));
        setIsAdmin(!!localStorage.getItem('isAdmin'));
        navigate(LOGIN_ROUTE);
        
    }
    return (
        <header className={styles.header}>
            <div className={styles.indent}></div>
            <div className={s.container}>
                <div className={styles.header__inner}>
                    <NavLink to={SHOP_ROUTE} className={styles.shop_title}>zeBook</NavLink>

                    <div className={styles.search}>
                        <input
                        className={styles.search__input}
                        placeholder="Поиск по названию"
                        value={search}
                        onChange={e => setSearch(e.target.value)} />
                    </div>

                    {state.isAuth ?
                        <nav>
                            {state.isAdmin ?
                                <div 
                                className={styles.navbar_Link}
                                onClick={() => {
                                    navigate(ADMIN_ROUTE)
                                }}>Админ панель</div>
                                :
                                <></>
                            }
                            <div
                            className={styles.navbar_Link}
                            onClick={() => {
                                navigate(BASKET_ROUTE)
                            }}>Корзина</div>
                            <div
                            className={styles.navbar_Link}
                            onClick={() => {
                                logout()
                            }}>Выйти</div>
                        </nav>
                        :
                        <button onClick={() => navigate(REGISTRATION_ROUTE)}>Авторизация</button>
                    }
                    
                </div>
            </div>
            
        </header>
    )
}

export default NavBar
