import { connect } from "react-redux"
import { setProductsAC, setSelectedGenreAC, setSelectedWriterAC, setTotalCountAC } from "../../store/products-reducer.js"
import { setIsAdminAC, setIsAuthAC } from "../../store/users-reducer.js"
import NavBar from "./Navbar.jsx"

let mapStateToProps = (state) => {
    return {
      userPage: state.userPage,
    }
  }


let mapDispatchToProps = (dispatch) => {
  return {
    setIsAuth: (bool) => {
      dispatch(setIsAuthAC(bool))
    },
    setIsAdmin: (bool => {
      dispatch(setIsAdminAC(bool))
    }),
    setProducts: (products) => {
      dispatch(setProductsAC(products))
    },
    setTotalCount: (totalCount) => {
      dispatch(setTotalCountAC(totalCount))
    },
    setSelectedGenre: (genre) => {
      dispatch(setSelectedGenreAC(genre))
    },
    setSelectedWriter: (writer) => {
      dispatch(setSelectedWriterAC(writer))
    }
  }
}



const NavBarContainer = connect(mapStateToProps, mapDispatchToProps)(NavBar)

export default NavBarContainer
