import React, { useState } from "react";
import s from '../../App.module.scss';
import CreateGenre from "../../components/modals/CreateGenre";
import CreateProduct from "../../components/modals/CreateProduct";
import CreateWriter from "../../components/modals/CreateWriter";
import styles from './Admin.module.scss';

const Admin = () => {
    const [writerVisible, setWriterVisible] = useState(false);
    const [genreVisible, setGenreVisible] = useState(false);
    const [productVisible, setProductVisible] = useState(false);

    return(
        <>
            <div className={` ${s.component} ${styles.admin}`}>
                <div className={s.container}>
                    <div className={styles.admin__btn}>Добавить Писателя</div>
                    <div className={styles.admin__btn}>Добавить Жанр</div>
                    <div className={styles.admin__btn}>Добавить Товар</div>
                </div>   
            </div>
            <CreateWriter />
            <CreateGenre />
            <CreateProduct />
        </>
    )
}

export default Admin;
