import React from "react";
import s from '../../App.module.scss'
import styles from './productPage.module.scss'

const ProductPage = () => {

    const product = {id: 1, name: "Пиковая дама", price: 26.49, rating: 5, img: "https://chitatel.by/storage/thumbs/b0/h1001_w1001_b0cdeabbb5751fa076c466a6fd9f07d9.jpg"};

    const desciption = [
        {id: 1, title: "Год издания", desciption: "2011"},
        {id: 2, title: "Колво страниц", desciption: "60"},
        {id: 3, title: "Тип обложки", desciption: "Мягкая обложка"},
        {id: 4, title: "Издательство", desciption: "ТетраСистемс"},
    ]
    return(
        <div className={` ${s.component} ${styles.productPage}`}>
            <div className={s.container}>
                <div className={styles.productName}>
                    {product.name}
                </div>
                <div className={styles.flex}>
                    <img className={styles.product_Image} src={product.img} />

                    <div className={styles.product_Info}>
                        <div className={styles.product_price}>{product.price} BYN/Шт</div>
                        {desciption.map(info => 
                            <div></div>
                        )}
                    </div>
                </div>
            </div>
        </div>
    )
}

export default ProductPage
