import React from "react";
import styles from './modal.module.scss';

const CreateWriter = (props) => {
    let show = props.show;
    debugger
    return(
        <div className={styles.modal}>
            <div className={styles.modal__content}>
                <div className={styles.modal__title}>Добавить автора</div>
                <form action="/" method="post">
                    <input className={styles.form__control} type="text" placeholder="Введите имя автора"></input>
                </form>

                <div className={styles.modal__buttons}>
                    <div className={` ${styles.modal__btn} ${styles.modal__add}`}>закрыть</div>
                    <div className={` ${styles.modal__btn} ${styles.modal__close}`}>добавить</div>
                </div>
            </div>
        </div>
    )
}

export default CreateWriter;
