import React from "react";
import { createGenre } from "../../http/productsAPI";
import styles from './modal.module.scss';

const CreateGenre = (props) => {
    const show = props.show;
    const [value, setValue] = useState('')
    const addGenre = () => {
        createGenre({name: value}).then(data => setValue(''))
    }
    return(
        <>
            {show
            ?
            <div className={styles.modal}>
                <div className={styles.modal__content}>
                    <div className={styles.modal__title}>Добавить жанр</div>
                    <form action="/" method="post">
                        <input 
                        className={styles.form__control} 
                        type="text" 
                        placeholder="Введите название жанра"
                        value={value}
                        onChange={e => setValue(e.target.value)}></input>
                    </form>

                    <div className={styles.modal__buttons}>
                        <div className={` ${styles.modal__btn} ${styles.modal__add}`}
                        onClick={() => props.onHide(false)}>
                            закрыть
                        </div>
                        <div className={` ${styles.modal__btn} ${styles.modal__close}`}
                        onClick={() => addGenre}>добавить</div>
                    </div>
                </div>
            </div>
            :
            <></>
        }
        </> 
    )
}

export default CreateGenre;
