import React, { useEffect } from "react";
import { cleanBasket, getBasket } from "../../http/productsAPI";
import styles from './basket.module.scss'
import s from '../../App.module.scss'
import { useNavigate } from "react-router-dom";
import { PRODUCTS_ROUTE, REGISTRATION_ROUTE } from "../../utils/consts";

const Basket = (props) =>  {
    const navigate = useNavigate();
    const productsPage = props.productsPage;
    const setBaskets = props.setBaskets;

    useEffect(() => {
        getBasket().then(data => {
            setBaskets(data)
        })
    }, [])

    let prices = 0;
    {productsPage.baskets.map(price =>
        
        prices = parseFloat((prices + price.product.price).toFixed(100))
    )}

    const cleanAllBasket = () => {
        cleanBasket().then(data => {
            setBaskets([])
        })
    }

    return (
        <div className={styles.basketPage}>
            <div className={s.container}>
                <div className={styles.basket_title}>Корзина</div>

                <div className={styles.total_basket}>
                    <div className={styles.total_price}>
                        <span>Итого: </span>{prices} BYN
                    </div>
                    <div className={styles.clean_basket}
                    onClick={cleanAllBasket}>
                        Очистить корзину
                    </div>
                </div>
                
                <div className={styles.basket_list}>
                    {productsPage.baskets.map(basketItem => 
                        //console.log(basketItem)
                        <div 
                        className={styles.basket_item}
                        onClick={() => navigate(PRODUCTS_ROUTE + '/' + basketItem.productId)}>
                            <div className={styles.basket_img}>
                                <img 
                                src={process.env.REACT_APP_API_URL + basketItem.product.image} alt="/" />
                            </div>

                            <div className={styles.basketItem__Inner}>
                                <div className={styles.productName}>
                                    {basketItem.product.title}
                                </div>
                                <div className={styles.productAuthor}>Автор</div>
                                <div className={styles.productsNameAuthor}>
                                    {basketItem.writer.name}
                                </div>
                                <div className={styles.productAuthor}>Жанр</div>
                                <div className={styles.productsNameAuthor}>
                                    {basketItem.genre.name}
                                </div>
                                <div className={styles.price}>{basketItem.product.price} BYN/Шт</div>
                            </div>
                        </div>
                    )}
                </div>
            </div>
        </div>
    )
}

export default Basket
