import React from 'react';
import s from '../../App.module.scss';
import style from './shop.module.scss'


const ShopSideBar = (props) => {
    let productsPage = props.productsPage;
    debugger
    return(
        <aside className={style.sidebar}>
            <ul className={style.sidebar__list}>

            {productsPage.genre.map(genre => 
                <li 
                key={genre.id} 
                data-active={genre.id === productsPage.selectedGenre.id}
                className={style.genre_item} 
                onClick={() => props.setSelectedGenre(genre)} >
                {genre.name}
                </li>  
            )}

            </ul>
        </aside>
    )
}

export default ShopSideBar
