import React from 'react';
import styles from './Navbar.module.scss'
import s from "../../App.module.scss"
import { NavLink, useNavigate } from 'react-router-dom';
import { ADMIN_ROUTE, BASKET_ROUTE, LOGIN_ROUTE, SHOP_ROUTE } from '../../utils/consts';

const NavBar = (props) => {
    debugger
    const state = props.userPage;
    const setIsAuth = props.setIsAuth;
    const navigate = useNavigate();

    let isCookieJWTAvaible = () => !!localStorage.getItem('token');
    
    const logout = (bool) => {
        localStorage.removeItem('token');
        setIsAuth(bool);
        navigate(LOGIN_ROUTE);
        isCookieJWTAvaible = () => !!localStorage.getItem('token');
    }
    return (
        <header className={styles.header}>
            <div className={s.container}>
                <div className={styles.header__inner}>
                    <NavLink to={SHOP_ROUTE}>zeBook</NavLink>

                    {isCookieJWTAvaible ?
                        <nav>
                            {state.isAdmin ?
                                <div onClick={() => {
                                    navigate(ADMIN_ROUTE)
                                }}>Админ панель</div>
                                :
                                <></>
                            }
                            <div onClick={() => {
                                navigate(BASKET_ROUTE)
                            }}>Корзина</div>
                            <div onClick={() => {
                                logout(false)
                            }}>Выйти</div>
                        </nav>
                        :
                        <button onClick={() => props.setIsAuth(true)}>Авторизация</button>
                    }
                    
                </div>
            </div>
            
        </header>
    )
}

export default NavBar
