import React, { useEffect, useState } from "react";
import { useNavigate, useParams } from "react-router-dom";
import { addToBasket, deleteProduct, getBasket, getRatingForProduct, postRatingForProduct, removeFromBasket, removeRatingForProduct, updateRatingForProduct } from "../../http/productsAPI";
import deletedtn from '../../assets/delete.svg';
import editbtn from '../../assets/edit.png';
import { SHOP_ROUTE } from "../../utils/consts";
import UpdateProduct from "../../components/modals/upgateProduct";
import { useDispatch, useSelector } from "react-redux";
import { fetchOneProductAction } from "../../redux/actions/products";

const ProductPage = (props) => {
    const dispatch = useDispatch();
    const {
        genre,
        writer,
        oneProduct,
        products,
        selectedWriter,
        selectedGenre,
        searchingProduct,
        totalCount
    } = useSelector(({ productsPage }) => productsPage);
    const isAdmin = useSelector(({ usersPage }) => usersPage.isAdmin);

    const productPage = props.productPage;
    const userPage = props.userPage;
    const setBaskets = props.setBaskets
    const navigate = useNavigate();
    const [productVisible, setProductVisible] = useState(false);

    const [product, setProduct] = useState({ info: [] })
    const [raitings, setRaitings] = useState([])
    const [inBasket, setInBasket] = useState(false)
    const { id } = useParams();



    useEffect(() => {
        getBasket().then(data => {
            setBaskets(data)

            data.map(product => {
                if (product.productId === Number(id)) {
                    setInBasket(true)
                }
            })
        })
        dispatch(fetchOneProductAction(id));
        getRatingForProduct(id).then(data => {
            setRaitings(data)
        })
    }, [])

    let submitedRate = false

    let ratingsCount = 0;
    let totalRatings = 0;
    let userRate = 0

    raitings.map(Ratings => {
        ratingsCount++;
        totalRatings += Ratings.rate;
        if (Ratings.userId === userPage.user.id) {
            userRate = Ratings.rate
            submitedRate = true
        }
    })
    let finalRating = (totalRatings / ratingsCount).toFixed(1)

    const postRate = (rate) => {
        if (submitedRate) {
            if (userRate === rate) {
                removeRatingForProduct(id).then(data => {
                    getRatingForProduct(id).then(data => {
                        setRaitings(data)
                    })
                })
            } else {
                updateRatingForProduct({
                    productId: Number(id),
                    rate
                }).then(data => {
                    getRatingForProduct(id).then(data => {
                        setRaitings(data)
                    })
                })
            }
        } else {
            postRatingForProduct({
                productId: Number(id),
                rate
            }).then(data => {
                getRatingForProduct(id).then(data => {
                    setRaitings(data)
                })
            })
        }

    }

    useEffect(() => {
        dispatch(fetchOneProductAction(id));
    }, [productVisible])


    const add = () => {
        addToBasket({
            productId: id,
            writerId: oneProduct.writerId,
            genreId: oneProduct.genreId
        }).then(data => {
            alert(`Товар ${oneProduct.title} был добавлен в вашу корзину!`)
            setInBasket(true)
        })
    }

    const removeBasketItem = () => {
        removeFromBasket(id).then(data => {
            alert(`Товар ${oneProduct.title} был удалён из корзины!`)
            setInBasket(false)
        })
    }

    const removeProduct = () => {
        deleteProduct(oneProduct.id).then(data => {
            navigate(SHOP_ROUTE)
        })
    }

    return (

        <div className="component productPage">
            <div className="container">
                <div className="productPage__inner">
                    <div className="productNamePage">
                        {oneProduct.title}
                    </div>
                    {!!isAdmin &&
                        <div>
                            <img
                                src={editbtn}
                                onClick={() => setProductVisible(true)}
                                title="Редактировать товар" />
                            <img
                                src={deletedtn}
                                onClick={removeProduct}
                                title="Удалить товар" />


                        </div>
                    }

                </div>

                <div className="flex">


                    <div className="flex_left">
                        <img
                            className="product_Image"
                            src={"http://localhost:5000/" + oneProduct.image} />
                        <div className="desciption">
                            Описание:
                            <div className="desciption_inner">
                                {oneProduct.productDescription}
                            </div>
                        </div>
                    </div>

                    <div className="product_Info">

                        {inBasket
                            ?
                            <>
                                <div className="Basket_desc">
                                    Товар в корзине
                                </div>
                                <div className="Basket"
                                    onClick={removeBasketItem}>
                                    Удалить из корзины
                                </div>
                            </>


                            :
                            <div className="Basket"
                                onClick={add}>
                                Добавить в корзину
                            </div>
                        }

                        <div className="product_price">
                            {oneProduct.price} BYN/Шт
                        </div>
                        {oneProduct.writer &&
                            <div className="product_Info_Inner">
                                Автор: {oneProduct.writer.name}
                            </div>}

                        {oneProduct.genre &&
                            <div className="product_Info_Inner">
                                Жанр: {oneProduct.genre.name}
                            </div>}
                        <div className="product_Info_Inner">
                            Год издания: {oneProduct.productionYear}
                        </div>
                        <div className="product_Info_Inner">
                            Количество страниц: {oneProduct.pageCount}
                        </div>
                        <div className="product_Info_Inner">
                            Издательство: {oneProduct.edition}
                        </div>



                    </div>
                </div>
            </div>

            <UpdateProduct
                show={productVisible}
                products={productPage}
                setWriter={props.setWriter}
                setGenre={props.setGenre}
                onHide={() => setProductVisible(false)} />
        </div >
    )
}

export default ProductPage
