import React, { useState } from 'react';
import s from "../../App.module.scss"
import { NavLink, useNavigate } from 'react-router-dom';
import { ADMIN_ROUTE, BASKET_ROUTE, LOGIN_ROUTE, REGISTRATION_ROUTE, SHOP_ROUTE } from '../../utils/consts';
import { fetchProducts } from '../../http/productsAPI';

const NavBar = (props) => {
    const userPage = props.userPage;
    const productsPage = props.productsPage;
    const setIsAuth = props.setIsAuth;
    const setIsAdmin = props.setIsAdmin;
    const setTotalCount = props.setTotalCount;
    const setProducts = props.setProducts;
    const setSelectedWriter = props.setSelectedWriter;
    const setSelectedGenre = props.setSelectedGenre;
    const setSearch = props.setSearchingProduct;

    const navigate = useNavigate();

    const search = productsPage.searchingProduct;

    const searchProduct = () => {
        if (search != 0) {
            setSelectedGenre({})
            setSelectedWriter({})
            navigate(SHOP_ROUTE)
            fetchProducts(null, null, 1, productsPage.limit, search.toLowerCase())
                .then(data => {
                    setProducts(data.rows)
                    setTotalCount(data.count)
                    if (data.count != 0) {
                        setSearch("")
                    }
                })
        }
    }

    const logout = () => {
        localStorage.removeItem('token');
        localStorage.removeItem('isAdmin');
        setIsAuth(!!localStorage.getItem('token'));
        setIsAdmin(!!localStorage.getItem('isAdmin'));
        navigate(LOGIN_ROUTE);

    }


    const zeBook = () => {
        setSelectedWriter({})
        setSelectedGenre({})
        setSearch("")
    }

    return (
        <header className="header">
            <div className="header__indent">

                {userPage.isAuth && userPage.isAdmin &&
                    <div
                        className={styles.navbar_Link}
                        onClick={() => {
                            navigate(ADMIN_ROUTE)
                        }}>Админ панель</div>
                }
            </div>
            <div className={s.container}>
                <div className={styles.header__inner}>
                    <NavLink
                        to={SHOP_ROUTE}
                        className={styles.shop_title}
                        onClick={zeBook}>zeBook</NavLink>

                    <div className={styles.search}>
                        <input id="navbar_search"
                            className={styles.search__input}
                            placeholder="Введите название"
                            value={search}
                            onChange={e => setSearch(e.target.value)}
                            autoComplete="off" />

                        <div className={styles.search_btn}
                            onClick={searchProduct}>
                            Поиск
                        </div>
                    </div>

                    {userPage.isAuth ?
                        <nav>

                            <div
                                className={styles.navbar_Link}
                                onClick={() => {
                                    navigate(BASKET_ROUTE)
                                }}>Корзина</div>
                            <div
                                className={styles.navbar_Link}
                                onClick={() => {
                                    logout()
                                }}>Выйти</div>
                        </nav>
                        :
                        <button onClick={() => navigate(REGISTRATION_ROUTE)}>Авторизация</button>
                    }

                </div>
            </div>

        </header>
    )
}

export default NavBar
