import React from 'react';
import s from '../../App.module.scss';
import styles from './shop.module.scss'

const Shop = observer(
  (props) => {

    let state = props.productsPage;
    console.log(state)
    return (
    <>
      <aside className={styles.sidebar}>
        <ul className={styles.sidebar__list}>

          {state.genre.map(genre => 
            <li 
            key={genre.id} 
            data-active={genre.id === state.productsPage}
            className={styles.genre_item} 
            onClick={() => props.setSelectedGenre(genre)} >
              {genre.name}
            </li>  
          )}

        </ul>
      </aside>
      <div className={s.container}>
        <div className={styles.shop}>Shopfffffffffffffffffffffffcddsvsdvsvsggggggggggggggggggggggggggggggggggggggggggggggvsvs</div>
      </div>
    </>
    )
}
) 

export default Shop;
