import React, { useEffect, useState } from "react";
import s from '../../App.module.scss'
import styles from './productPage.module.scss'
import { useParams } from "react-router-dom";
import { fetchOneProduct } from "../../http/productsAPI";
import deletedtn from '../../assets/delete.png'

const ProductPage = (props) => {
    const productPage = props.productPage;
    const userPage = props.userPage
    

    const[product, setProduct] = useState({info: []})
    const {id} = useParams();
    
    
    useEffect(() => {
        fetchOneProduct(id).then(data => setProduct(data))
    }, [])
    console.log(product)
    return(
        <div className={` ${s.component} ${styles.productPage}`}>
            <div className={s.container}>
                <div className={styles.productPage__inner}>
                    <div className={styles.productName}>
                        {product.title}
                    </div>
                    {!!userPage.isAdmin && 
                    <></> }
                    
                </div>

                <div className={styles.flex}>
                    <img className={styles.product_Image} 
                    src={"http://localhost:5000/" + product.image} />
                    <div className={styles.product_Info}>

                        <div className={styles.addToBasket}>
                            Добавить в корзину
                        </div>
                        <div className={styles.product_price}>
                            {product.price} BYN/Шт
                        </div>
                        {product.writer && 
                        <div className={styles.product_Info_Inner}>
                            Автор: {product.writer.name}
                        </div>}
                        
                        {product.genre && 
                        <div className={styles.product_Info_Inner}>
                            Жанр: {product.genre.name}
                        </div>}
                        <div className={styles.product_Info_Inner}>
                            Год издания: {product.productionYear}
                        </div>
                        <div className={styles.product_Info_Inner}>
                            Количество страниц: {product.pageCount}
                        </div>
                        <div className={styles.product_Info_Inner}>
                            Издательство: {product.edition}
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default ProductPage
