import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { fetchGenres, fetchOneProduct, fetchWriters, updateProduct } from "../../http/productsAPI";
import styles from './modal.module.scss';

const UpdateProduct = (props) => {
    debugger
    
    const onHide = props.onHide
    const show = props.show;
    const selectedProduct = props.product;
    const productsPage = props.products;
    
    const[product, setProduct] = useState({info: []})
    const {id} = useParams();

    useEffect(() => {
        fetchOneProduct(id).then(data => {
            setProduct(data)
            console.log(data)})
    }, [])
    
    
    
    const [name, setName] = useState(product.title)
    const [writer, setWriters] = useState(product.writer);
    const [genre, setGenres] = useState(product.genre);
    // const [writerName, setWriterName] = useState(product.writer.name)
    // const [genreName, setGenreName] = useState(product.genre.name)
    const [price, setPrice] = useState(selectedProduct.price)
    const [file, setFile] = useState(null)
    const [edition, setEdition] = useState(selectedProduct.edition)
    const [productYear, setProductYear] = useState(selectedProduct.productionYear)
    const [pageCount, setPageCount] = useState(selectedProduct.pageCount)
    const [description, setDescription] = useState(selectedProduct.productDescription)

  

    const selectFile =  (e) => {
        setFile(e.target.files[0])
    }
    useEffect(() => {
        setName(selectedProduct.title);
        setWriters(selectedProduct.writer);
        setGenres(selectedProduct.genre)
        setPrice(selectedProduct.price);
        setEdition(selectedProduct.edition);
        setProductYear(selectedProduct.productionYear);
        setPageCount(selectedProduct.pageCount);
        setDescription(selectedProduct.productDescription);
    }, [show])
    

    const editProduct = () => {
        const formData = new FormData();
        try {
            formData.append('title', name)
            formData.append('price', Number(price))
            formData.append('image', file)
            formData.append('genreId', Number(genre.id))
            formData.append('writerId', Number(writer.id))
            formData.append('productionYear', Number(productYear))
            formData.append('pageCount', Number(pageCount))
            formData.append('edition', edition)
            formData.append('productDescription', description)
            debugger
            updateProduct(selectedProduct.id, formData).then(data => {
                
                onHide(false)
            })
        } catch (e) {
            alert("Неудалось добавить товар")
        }
    }

    const setWriter = (writer) => {
        setWriters(writer);
        //setWriterName(writer.name)
    }

    const setGenre = (genre) => {
        setGenres(genre);
        //setGenreName(genre.name)
        debugger
    }

    return(
        <>
            {show
            ?
            <div className={styles.modal}>
                <div className={styles.modal__content}>
                    <div className={styles.modal__title}>Добавить товар</div>
                    <form action="/" method="post">

                        <div className={styles.drop}>
                            <div className={styles.drop_name}>
                                Автор: {writer.name}
                            </div>
                            <div className={styles.dropdown}>
                                
                                <div className={styles.listTitle}>Выбрать автора</div>
                                <div className={styles.dropdown_content}>
                                {productsPage.writer.map(writers =>
                                    <div 
                                    className={styles.dropdown_item}
                                    onClick={() => setWriter(writers)}
                                    key={writers.id}
                                    >
                                        {writers.name}
                                    </div>
                                )}
                                </div>
                            </div>
                        </div>

                        <div className={styles.drop}>
                            <div className={styles.drop_name}>
                                Жанр: {genre.name}
                            </div>
                            <div className={styles.dropdown}>
                                
                                <div className={styles.listTitle}>Выбрать жанр</div>
                                <div className={styles.dropdown_content}>
                                {productsPage.genre.map(genres =>
                                    <div 
                                    className={styles.dropdown_item}  
                                    onClick={() => setGenre(genres)}
                                    key={genres.id}>
                                        {genres.name}
                                    </div>
                                )}
                                </div>
                            </div>
                        </div>

                        <div className={styles.form__inner}>
                            <div className={styles.form_title}>Название товара:</div>
                            <input 
                            className={styles.form__control} 
                            type="text" 
                            placeholder="Введите название товара"
                            value={name}
                            onChange={e => setName(e.target.value)}
                            ></input>
                        </div>
                        
                        <div className={styles.form__inner}>
                            <div className={styles.form_title}>Стоимость товара:</div>
                            <input 
                            className={styles.form__control} 
                            type="text" 
                            placeholder="Введите стоимость товара"
                            value={price}
                            onChange={e => setPrice(e.target.value)}
                            ></input>
                        </div>
                        
                        <div className={styles.form__inner}>
                            <div className={styles.form_title}>Издательство:</div>
                            <input 
                            className={styles.form__control} 
                            type="text" 
                            placeholder="Введите издательство"
                            value={edition}
                            onChange={e => setEdition(e.target.value)}
                            ></input>
                        </div>
                        
                        <div className={styles.form__inner}>
                            <div className={styles.form_title}>Год выпуска:</div>
                            <input 
                            className={styles.form__control} 
                            type="text" 
                            placeholder="Введите год выпуска товара"
                            value={productYear}
                            onChange={e => setProductYear(e.target.value)}
                            ></input>
                        </div>
                        
                        <div className={styles.form__inner}>
                            <div className={styles.form_title}>Количество страниц:</div>
                            <input 
                            className={styles.form__control} 
                            type="text" 
                            placeholder="Введите количество страниц"
                            value={pageCount}
                            onChange={e => setPageCount(e.target.value)}
                            ></input>
                        </div>
                        
                        <div className={styles.form__inner}>
                            <div className={styles.form_title}>Описание товара:</div>
                            <textarea 
                            className={styles.form__textarea} 
                            type="text" 
                            placeholder="Введите описание товара"
                            value={description}
                            onChange={e => setDescription(e.target.value)}
                            ></textarea>
                        </div>
                        

                        <div className={styles.addIMG}>Выберите изображение:</div>
                        <input
                        type="file"
                        onChange={selectFile}
                        ></input>
                    </form>

                    <div className={styles.modal__buttons}>
                        <div className={` ${styles.modal__btn} ${styles.modal__add}`}
                        onClick={() => onHide(false)}>
                            закрыть
                        </div>
                        <div 
                        className={` ${styles.modal__btn} ${styles.modal__close}`}
                        onClick={editProduct}>Сохранить изменения</div>
                    </div>
                </div>
            </div>
            :
            <></>
        }
        </> 
    )
}

export default UpdateProduct;
