import React from 'react';
import styles from './shop.module.scss'

const GenreBar = (props) => {
    let productsPage = props.productsPage;
    debugger
    return (
        <ul className={styles.genreBar}>

            {productsPage.genre.map(genre => 
                <li 
                key={genre.id} 
                data-active={genre.id === productsPage.selectedWriter.id}
                className={styles.genre_item} 
                onClick={() => props.setSelectedWriter(genre)} >
                {genre.name}
                </li>  
            )}

        </ul>
    )
}

export default GenreBar
