import React from 'react';
import s from '../../App.module.scss';
import styles from './shop.module.scss'


const WriterBar = (props) => {
    let productsPage = props.productsPage;

    const setWriter = writer => {
        writer.id === productsPage.selectedWriter.id
        ?
        props.setSelectedGenre({})
        :
        props.setSelectedGenre(writer)
    }
    
    return(
        <aside className={styles.sidebar}>
            <ul className={styles.sidebar__list}>

            {productsPage.writer.map(writer => 
                <li 
                key={writer.id} 
                data-active={writer.id === productsPage.selectedWriter.id}
                className={styles.writer_item} 
                onClick={() => props.setSelectedWriter(writer)} >
                {writer.name}
                </li>  
            )}

            </ul>
        </aside>
    )
}

export default WriterBar
