import { connect } from "react-redux"
import { setBasketsAC, setGenreAC, setProductsAC, setSelectedGenreAC, setSelectedWriterAC, setWriterAC } from "../../store/products-reducer"
import ProductPage from "./ProductPage"

let mapStateToProps = (state) => {
    return {
      productPage: state.productsPage,
      userPage: state.userPage
    }
    
}

let mapDispatchToProps = (dispatch) => {
  return {
    setGenre: (genre) => {
      dispatch(setGenreAC(genre))
    },
    setWriter: (writer) => {
      dispatch(setWriterAC(writer))
    },
    setSelectedGenre: (selectedGenre) => {
      dispatch(setSelectedGenreAC(selectedGenre))
    },
    setSelectedWriter: (selectedWriter) => {
      dispatch(setSelectedWriterAC(selectedWriter))
    },
    setBaskets: (basket) => {
      dispatch(setBasketsAC(basket))
    }
  }
  
}


const AppRouterContainer = connect(mapStateToProps, mapDispatchToProps)(ProductPage)

export default AppRouterContainer
