export const setUser = (user) => ({
    type: 'SET_USER',
    user
})

export const setIsAdmin = (bool) => ({
    type: 'SET_IS_ADMIN',
    bool
})

export const setIsAuth = (bool) => ({
    type: 'SET_IS_AUTH',
    bool
})
