import React, { useEffect, useState } from "react";
import s from '../../App.module.scss'
import styles from './productPage.module.scss'
import { useParams } from "react-router-dom";
import { fetchOneProduct } from "../../http/productsAPI";

const ProductPage = (props) => {
    const productPage = props.productPage;

    const[product, setProduct] = useState({info: []})
    const {id} = useParams();
    
    useEffect(() => {
        fetchOneProduct(id)
    }, [])
    return(
        <div className={` ${s.component} ${styles.productPage}`}>
            <div className={s.container}>
                <div className={styles.productName}>
                    {product.name}
                </div>

                <div className={styles.flex}>
                    <img className={styles.product_Image} 
                    src={product.img} />
                    <div className={styles.product_Info}>

                        <div className={styles.addToBasket}>
                            Добавить в корзину
                        </div>
                        <div className={styles.product_price}>
                            {product.price} BYN/Шт
                        </div>
                        {productPage.products.map(info => 
                            <div key={info.id}>
                                {info.title}: l
                            </div>
                        )}
                    </div>
                </div>
            </div>
        </div>
    )
}

export default ProductPage
