        {id: 7, name: "Драма"},
        {id: 1, name: "Фантастика"},
        {id: 2, name: "Драма"},
        {id: 3, name: "Триллер"},
        {id: 4, name: "Сказка"},
        {id: 6, name: "АртХаус"},
        {id: 7, name: "Драма"},
    ],
    writer: [
        {id: 1, name: "Александр Пушкин"},
        {id: 2, name: "Фёдор Достоевский"},
        {id: 3, name: "Стивен Кинг"},
        {id: 4, name: "Лев Толстой"},
        {id: 6, name: "Николай Гоголь"},
        {id: 7, name: "Антон Чехов"},
        
        
    ],
    products: [],
    baskets: [],
    selectedGenre: {},
    selectedWriter: {},
    page: 1,
    totalCount: 0,
    limit: 2
};

const productsReducer = (state = initialState, action) => {

    switch (action.type){
        case SET_GENRE: 
            return {...state, genre: action.genre}

        case SET_WRITER: 
            return {...state, writer: action.writer}

        case SET_PRODUCTS: 
            return {...state, products: action.products}

        case SET_BASKETS: 
            return {...state, baskets: action.baskets}

        case SET_SELECTED_GENRE: 
            return {...state, page: 1, selectedGenre: action.selectedGenre}

        case SET_SELECTED_WRITER: 
            return {...state, page: 1, selectedWriter: action.selectedWriter}

        case SET_PAGE: 
            return {...state, page: action.page}

        case SET_TOTAL: 
            return {...state, totalCount: action.totalCount}

        default:
            return state;
    }
} 

export const setGenre = (genre) => ({type: SET_GENRE, genre}); 
export const setWriter = (writer) => ({type: SET_GENRE, writer}); 
export const setProducts = (products) => ({type: SET_PRODUCTS, products}); 
export const setBaskets = (baskets) => ({type: SET_BASKETS, baskets}); 
export const setSelectedGenre = (selectedGenre) => ({type: SET_SELECTED_GENRE, selectedGenre}); 
export const setSelectedWriter = (selectedWriter) => ({type: SET_SELECTED_WRITER, selectedWriter}); 
export const setPage = (page) => ({type: SET_PAGE, page}); 
export const setTotalCount = (totalCount) => ({type: SET_TOTAL, totalCount}); 


export default productsReducer;
