import React from 'react';
import { NavLink, useNavigate } from 'react-router-dom';
import { PRODUCTS_ROUTE, REGISTRATION_ROUTE, SHOP_ROUTE } from '../../utils/consts';
import styles from './shop.module.scss'

const ProductItem = (props) => {

    const navigate = useNavigate();

    const productGenre = props.productGenre
    const productsName = props.productsName;
    const productId = props.productId;
    const productImg = props.productImg;
    const productAuthor = props.productAuthor
    const price = props.price

    return (
        <div className={styles.productsItem}
        onClick={() => navigate(PRODUCTS_ROUTE + '/' + productId) }>
            
            <div className={styles.productLink}>
                <img src={"http://localhost:5000/" + productImg} alt="/" />
            </div>
            <div className={styles.productsItem__Inner}>
                <div className={styles.productName} to={REGISTRATION_ROUTE}>
                    {productsName}
                </div>
                <div className={styles.productAuthor}>Автор</div>
                <div className={styles.productsNameAuthor}>
                    {productAuthor}
                </div>
                <div className={styles.productAuthor}>Жанр</div>
                <div className={styles.productsNameAuthor}>
                    {productGenre}
                </div>
                <div className={styles.price}>{price} BYN/Шт</div>
            </div>
        </div>
    )
}

export default ProductItem;
