import React, { useEffect } from "react";
import { cleanBasket, getBasket } from "../../http/productsAPI";
import styles from './basket.module.scss'
import s from '../../App.module.scss'

const Basket = (props) =>  {

    const productsPage = props.productsPage;
    const setBaskets = props.setBaskets;

    useEffect(() => {
        getBasket().then(data => {
            setBaskets(data)
        })
    }, [])

    let prices = 0;
    {productsPage.baskets.map(price =>
        prices += price.product.price
    )}

    const cleanAllBasket = () => {
        cleanBasket().then(data => {
            console.log(data)
        })
    }

    return (
        <div className={styles.basketPage}>
            <div className={s.container}>
                <div className={styles.basket_title}>Корзина</div>

                <div className={styles.total_basket}>
                    <div className={styles.total_price}>
                        <span>Итого: </span>{prices} BYN
                    </div>
                    <div className={styles.clean_basket}
                    onClick={cleanAllBasket}>
                        Очистить корзину
                    </div>
                </div>
                
            </div>
        </div>
    )
}

export default Basket
