import { connect } from "react-redux"
import { setIsAdminAC, setIsAuthAC } from "../../store/users-reducer.js"
import pagination from "./pagination.jsx"

let mapStateToProps = (state) => {
    return {
      product: state.productPage,
    }
  }


let mapDispatchToProps = (dispatch) => {
  return {
    setIsAuth: (bool) => {
      dispatch(setIsAuthAC(bool))
    },
    setIsAdmin: (bool => {
      dispatch(setIsAdminAC(bool))
    })
  }
}



const NavBarContainer = connect(mapStateToProps, mapDispatchToProps)(pagination)

export default NavBarContainer
