import React, { useEffect, useState } from "react";
import s from '../../App.module.scss'
import styles from './productPage.module.scss'
import { useNavigate, useParams } from "react-router-dom";
import { addToBasket, deleteProduct, fetchOneProduct, getBasket, getRatingForProduct, removeFromBasket } from "../../http/productsAPI";
import deletedtn from '../../assets/delete.svg';
import star from '../../assets/star2.svg';
import editbtn from '../../assets/edit.png';
import { SHOP_ROUTE } from "../../utils/consts";
import UpdateProduct from "../../components/modals/upgateProduct";

const ProductPage = (props) => {
    const productPage = props.productPage;
    const userPage = props.userPage;
    const setBaskets = props.setBaskets
    const navigate = useNavigate();
    const [productVisible, setProductVisible] = useState(false);

    const[product, setProduct] = useState({info: []})
    const[raitings, setRaitings] = useState([])
    const[inBasket, setInBasket] = useState(false)
    const {id} = useParams();

    useEffect(() => {
        getBasket().then(data => {
            setBaskets(data)

            data.map(product => {
                if(product.productId === Number(id)){
                    setInBasket(true)
                }
            })
        })
        fetchOneProduct(id).then(data => setProduct(data))
        getRatingForProduct(id).then(data => {
            //console.log(data);
            setRaitings(data)
        })
    }, [])

    let ratingsCount = 0;
    let totalRatings = 0;
    raitings.map(Ratings => {
        ratingsCount++;
        totalRatings += Ratings.rate
    })
    let finalRating = totalRatings/ratingsCount

    useEffect(() => {
        fetchOneProduct(id).then(data => setProduct(data))
    }, [productVisible])


    const add = () => {
        addToBasket({
            productId: Number(id),
            writerId: product.writerId,
            genreId: product.genreId
        }).then(data => {
            alert(`Товар ${product.title} был добавлен в вашу корзину!`)
            setInBasket(true)
        })
    }

    const removeBasketItem = () => {
        removeFromBasket(id).then(data => {
            alert(`Товар ${product.title} был удалён из корзины!`)
            setInBasket(false)
        })
    }

    const removeProduct = () => {
        deleteProduct(product.id).then(data => {
            navigate(SHOP_ROUTE)
        })
    }

    return(
        
        <div className={` ${s.component} ${styles.productPage}`}>
            <div className={s.container}>
                <div className={styles.productPage__inner}>
                    <div className={styles.productName}>
                        {product.title}
                    </div>
                    {!!userPage.isAdmin && 
                    <div>
                        <img 
                        src={editbtn}
                        onClick={() => setProductVisible(true)}
                        title="Редактировать товар" />
                        <img 
                        src={deletedtn}
                        onClick={removeProduct}
                        title="Удалить товар" />
                        

                    </div>
                    }
                    
                </div>

                <div className={styles.flex}>
                    

                    <div className={styles.flex_left}>
                        <img 
                        className={styles.product_Image}
                        src={"http://localhost:5000/" + product.image} />
                        <div className={styles.desciption}>
                            Описание: 
                            <div className={styles.desciption_inner}>
                                {product.productDescription}
                            </div>
                        </div>
                    </div>

                    <div className={styles.product_Info}>

                        {inBasket 
                        ?
                        <>
                            <div className={styles.Basket_desc}>
                                Товар в корзине
                            </div>
                            <div className={styles.Basket}
                            onClick={removeBasketItem}>
                                Удалить из корзины
                            </div>
                        </>
                        
                        
                        :
                        <div className={styles.Basket}
                        onClick={add}>
                            Добавить в корзину
                        </div>
                        }
                        
                        <div className={styles.product_price}>
                            {product.price} BYN/Шт
                        </div>
                        {product.writer && 
                        <div className={styles.product_Info_Inner}>
                            Автор: {product.writer.name}
                        </div>}
                        
                        {product.genre && 
                        <div className={styles.product_Info_Inner}>
                            Жанр: {product.genre.name}
                        </div>}
                        <div className={styles.product_Info_Inner}>
                            Год издания: {product.productionYear}
                        </div>
                        <div className={styles.product_Info_Inner}>
                            Количество страниц: {product.pageCount}
                        </div>
                        <div className={styles.product_Info_Inner}>
                            Издательство: {product.edition}
                        </div>

                        <div className={styles.product_Info_Inner}>
                            Рейтинг: {isNaN(finalRating) ? "нет оценок" : finalRating} 
                        </div>

                        <div className={styles.Stars}>
                            <img src={star} />
                        </div>
                    </div>
                </div>
            </div>

            <UpdateProduct 
            show={productVisible}
            products={productPage}
            setWriter={props.setWriter}
            setGenre={props.setGenre}
            onHide={() => setProductVisible(false)} />
        </div>
    )
}

export default ProductPage
