import React from 'react';
import styles from './shop.module.scss'
import s from '../../App.module.scss';
const GenreBar = (props) => {
    let productsPage = props.productsPage;

    const setGenre = genre => {
        genre.id === productsPage.selectedGenre.id
        ?
        props.setSelectedGenre({})
        :
        props.setSelectedGenre(genre)
    }

    
    return (
        <ul className={`${styles.genreBar} ${s.component}`}>

            {productsPage.genre.map(genre => 
                <li 
                key={genre.id} 
                data-active={genre.id === productsPage.selectedGenre.id}
                className={styles.genre_item} 
                onClick={() => setGenre(genre) } >
                {genre.name}
                </li>  
            )}

        </ul>
    )
}

export default GenreBar
