import React from "react";
import styles from './modal.module.scss';

const CreateProduct = (props) => {
    const show = props.show;
    const products = props.products;
    const setGenre = props.setGenre;
    const setWriter = props.setWriter;

    const [name, setName] = useState('')
    const [price, setPrice] = useState(0)
    const [file, setFile] = useState(null)
    const [edition, setEdition] = useState('')
    const [productYear, setProductYear] = useState(0)
    const [pageCount, setPageCount] = useState(0)
    const [description, setDescription] = useState('')

    useEffect(() => {
        fetchTypes().then(data => device.setTypes(data))
        fetchBrands().then(data => device.setBrands(data))
    }, [])
    
    debugger
    return(
        <>
            {show
            ?
            <div className={styles.modal}>
                <div className={styles.modal__content}>
                    <div className={styles.modal__title}>Добавить товар</div>
                    <form action="/" method="post">
                        <input 
                        className={styles.form__control} 
                        type="text" 
                        placeholder="Введите название товара"
                        value={name}
                        onChange={e => setName(e.target.value)}
                        ></input>

                        <input 
                        className={styles.form__control} 
                        type="text" 
                        placeholder="Введите стоимость товара"
                        value={price}
                        onChange={e => setPrice(e.target.value)}
                        ></input>

                        <input 
                        className={styles.form__control} 
                        type="text" 
                        placeholder="Введите издание товара"
                        value={edition}
                        onChange={e => setEdition(e.target.value)}
                        ></input>

                        <input 
                        className={styles.form__control} 
                        type="text" 
                        placeholder="Введите год выпуска товара"
                        value={productYear}
                        onChange={e => setProductYear(e.target.value)}
                        ></input>

                        <input 
                        className={styles.form__control} 
                        type="text" 
                        placeholder="Введите количество страниц"
                        value={pageCount}
                        onChange={e => setPageCount(e.target.value)}
                        ></input>

                        <input 
                        className={styles.form__control} 
                        type="text" 
                        placeholder="Введите описание товара"
                        value={description}
                        onChange={e => setDescription(e.target.value)}
                        ></input>
                    </form>

                    <div className={styles.modal__buttons}>
                        <div className={` ${styles.modal__btn} ${styles.modal__add}`}
                        onClick={() => props.onHide(false)}>
                            закрыть
                        </div>
                        <div className={` ${styles.modal__btn} ${styles.modal__close}`}>добавить</div>
                    </div>
                </div>
            </div>
            :
            <></>
        }
        </> 
    )
}

export default CreateProduct;
