import React, { useEffect } from 'react';
import { fetchGenres, fetchProducts, fetchWriters } from '../../http/productsAPI';
import GenreBar from './GenreBar';
import ProductsList from './ProductsList';
import WriterBar from './WriterBar';
import Pagination from '../../components/pagination/pagination'
import { useDispatch } from 'react-redux';
import { fetchByCategory } from '../../redux/actions/products';

const Shop = (props) => {
    const dispatch = useDispatch();
    const {
        selectedWriter,
        selectedGenre,
        page,
        limit,
        searchingProduct
    } = useSelector(({ productsPage }) => productsPage)
    const state = props.productsPage;

    const setProducts = props.setProducts;
    const setTotalCount = props.setTotalCount;
    const setPage = props.setPage;

    useEffect(() => {
        dispatch(fetchByCategory());
    }, [])

    useEffect(() => {
        fetchProducts(state.selectedWriter.id, state.selectedGenre.id, state.page, state.limit, state.searchingProduct).then(data => {
            setProducts(data.rows)
            setTotalCount(data.count)
        })
    }, [state.page, state.selectedGenre, state.selectedWriter])

    return (
        <div className="shop component">

            <WriterBar
                productsPage={state}
                setSelectedWriter={props.setSelectedWriter} />

            <div className="container">
                <GenreBar
                    productsPage={state}
                    setSelectedGenre={props.setSelectedGenre} />

                <div className="Products">
                    <ProductsList productsPage={state} />
                </div>

                {state.products.length === 0 &&
                    <div>Упс, походу ничего не найдено</div>
                }
                <Pagination
                    productsPage={state}
                    setPage={setPage} />
            </div>
        </div>
    )
}

export default Shop;
